// Helper function to get logo by provider name
export const getProviderLogo = (provider: string) => {
  const providerMap: Record<string, typeof AlibabaLogo> = {
    alibaba: AlibabaLogo,
    amazon: AmazonLogo,
    anthropic: AnthropicLogo,
    cohere: CohereLogo,
    deepseek: DeepseekLogo,
    google: GoogleLogo,
    inception: InceptionLogo,
    meituan: MeituanLogo,
    meta: MetaLogo,
    mistral: MistralLogo,
    moonshotai: MoonshotaiLogo,
    morph: MorphLogo,
    openai: OpenAILogo,
    perplexity: PerplexityLogo,
    stealth: StealthLogo,
    vercel: VercelLogo,
    xai: XAILogo,
    zai: ZaiLogo,
  };

  const normalizedProvider = provider.toLowerCase().replace(/\s+/g, "");
  return providerMap[normalizedProvider] || OpenAILogo; // Default fallback
};
